fn select_words(s: &str, n: i32) -> Vec<String> {
    let mut words = s.split_whitespace().map(|word| word.to_string()).collect::<Vec<String>>();
    let mut consonants = 0;
    for word in words {
        let mut consonant_count = 0;
        for c in word.chars() {
            if c.is_alphabetic() {
                consonant_count += 1;
                if consonant_count == 1 && c.is_lowercase() {
                    consonants += 1;
                }
            }
        }
        if consonants == n {
            words.push(word.to_string());
        }
    }
    words
}

