fn min_path(grid: Vec<Vec<i32>>, k: i32) -> Vec<i32> {
    let n = grid.len();
    let m = grid[0].len();
    let mut dp = vec![vec![0; m]; n];
    for i in 0..n {
        for j in 0..m {
            dp[i][j] = grid[i][j];
        }
    }
    let mut visited = vec![false; n * m];
    let mut queue = Vec::new();
    queue.push((0, 0, 0, 0, 0));
    visited[(0 * m + 0) as usize] = true;
    while let Some((x, y, dist, prev_x, prev_y)) = queue.pop() {
        if dist == k {
