fn tri(n: i32) -> Vec<i32> {
    let mut result = vec![0; n + 1];
    result[1] = 3;
    if n % 2 == 0 {
        for i in 2..n + 1 {
            result[i] = 1 + (i / 2.0);
        }
    } else {
        for i in 2..n + 1 {
            result[i] = tri(i - 1) + tri(i - 2) + tri(i + 1);
        }
    }
    result
}

